import React, { useState } from 'react';
import '../../styles/HomePage/CallToAction.css';

const faqs = [
  {
    question: "What is the process for booking an appointment?",
    answer: "To book an appointment, simply select your specialty, choose a doctor, and schedule a time that works best for you. You can then proceed with an audio or video consultation."
  },
  {
    question: "How do I pay for the consultation?",
    answer: "Payments can be made through various online methods, including credit/debit cards, UPI, or digital wallets."
  },
  {
    question: "Is the consultation secure?",
    answer: "Yes, our platform uses end-to-end encryption to ensure your consultations are secure and confidential."
  },
  {
    question: "Can I get a prescription after the consultation?",
    answer: "Yes, after your consultation, you will receive a digital prescription. You can also get a follow-up consultation if needed."
  },
  {
    question: "Do you offer any discounts for first-time users?",
    answer: "Yes, we offer special discounts for first-time users. Check our website or app for ongoing promotions."
  }
];

const CallToAction = () => {
  const [activeIndex, setActiveIndex] = useState(null);

  const toggleAnswer = (index) => {
    if (index === activeIndex) {
      setActiveIndex(null); // Close the answer if clicked again
    } else {
      setActiveIndex(index); // Open the answer
    }
  };

  return (
    <div className="cta-section">
      <h2 className="cta-title">Have Questions? We’ve Got Answers!</h2>
      <div className="faq-list">
        {faqs.map((faq, index) => (
          <div key={index} className="faq-item">
            <div 
              className="faq-question" 
              onClick={() => toggleAnswer(index)}
            >
              <h3>{faq.question}</h3>
              <span className={`arrow ${activeIndex === index ? 'open' : ''}`}>▶</span>
            </div>
            {activeIndex === index && (
              <div className="faq-answer">
                <p>{faq.answer}</p>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default CallToAction;
