import React from "react";
import Navbar from "../components/Navbar";
import Services from "../components/Services";
import Testimonials from "../components/Testimonials";
import Contact from "../components/Contact";
import heroImage from "../assets/hero-image.jpg"; // Use an actual image

const Home = () => {
  return (
    <div>
      <Navbar />

      {/* Hero Section */}
      <header className="w-full h-screen flex flex-col justify-center items-center bg-blue-50 text-center px-8">
        <h1 className="text-5xl md:text-6xl font-extrabold text-blue-700 mb-6">MediCare</h1>
        <p className="text-2xl md:text-3xl text-gray-700 font-medium">Your Health, Our Priority</p>
        <img src={heroImage} alt="Healthcare" className="w-full max-w-4xl mt-8 rounded-lg shadow-lg" />
      </header>

      {/* About Section */}
      <section className="w-full px-8 py-20 bg-white text-center">
        <h2 className="text-4xl font-bold text-gray-800">About Us</h2>
        <p className="text-xl text-gray-600 mt-4 max-w-2xl mx-auto">
          We provide online consultations, appointment bookings, and digital prescriptions to make healthcare accessible for everyone.
        </p>
      </section>

      {/* Services Section */}
      <Services />

      {/* Testimonials Section */}
      <Testimonials />

      {/* Contact Section */}
      <Contact />
    </div>
  );
};

export default Home;
