import React, { useState } from "react";
import Step1BasicDetails from "./Step1BasicDetails";
import Step2PharmacyDetails from "./Step2PharmacyDetails";
import Step3UploadDocuments from "./Step3UploadDocuments";
import Step4ReviewSubmit from "./Step4ReviewSubmit";
import "../../styles/PharmacyRegistration.css"; // Import styles

const MultiStepForm = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    licenseNumber: "",
    pharmacyName: "",
    address: "",
    city: "",
    state: "",
    pincode: "",
    licenseFile: null,
    pharmacyPhoto: null,
  });

  const nextStep = () => setStep(step + 1);
  const prevStep = () => setStep(step - 1);

  const handleChange = (e) => {
    const { name, value, type, files } = e.target;
    if (type === "file") {
      setFormData({ ...formData, [name]: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  return (
    <div className="pharmacy-registration-container">
      {step === 1 && (
        <Step1BasicDetails nextStep={nextStep} handleChange={handleChange} formData={formData} />
      )}
      {step === 2 && (
        <Step2PharmacyDetails nextStep={nextStep} prevStep={prevStep} handleChange={handleChange} formData={formData} />
      )}
      {step === 3 && (
        <Step3UploadDocuments nextStep={nextStep} prevStep={prevStep} handleChange={handleChange} formData={formData} />
      )}
      {step === 4 && <Step4ReviewSubmit prevStep={prevStep} formData={formData} />}
    </div>
  );
};

export default MultiStepForm;
