import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Register = () => {
  const [userType, setUserType] = useState("doctor"); // Default user type
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
    licenseNumber: "",
    specialization: "",
    experience: "",
    age: "",
    gender: "",
    phone: "",
    location: ""
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.password !== formData.confirmPassword) {
      alert("Passwords do not match!");
      return;
    }
    try {
      const apiUrl = `/api/${userType}s/register`;
      await axios.post(apiUrl, formData);
      alert("Registration Successful! Please login.");
      navigate("/login");
    } catch (error) {
      alert(error.response?.data?.message || "Registration failed");
    }
  };

  return (
    <div>
      <h2>Register</h2>
      <select onChange={(e) => setUserType(e.target.value)} value={userType}>
        <option value="doctor">Doctor</option>
        <option value="patient">Patient</option>
        <option value="pharmacy">Pharmacy</option>
      </select>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
        <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
        <input type="password" name="password" placeholder="Password" onChange={handleChange} required />
        <input type="password" name="confirmPassword" placeholder="Confirm Password" onChange={handleChange} required />
        {userType === "doctor" && (
          <>
            <input type="text" name="licenseNumber" placeholder="License Number" onChange={handleChange} required />
            <input type="text" name="specialization" placeholder="Specialization" onChange={handleChange} required />
            <input type="number" name="experience" placeholder="Years of Experience" onChange={handleChange} required />
          </>
        )}
        {userType === "patient" && (
          <>
            <input type="number" name="age" placeholder="Age" onChange={handleChange} required />
            <input type="text" name="gender" placeholder="Gender" onChange={handleChange} required />
          </>
        )}
        {userType === "pharmacy" && (
          <>
            <input type="text" name="licenseNumber" placeholder="License Number" onChange={handleChange} required />
            <input type="text" name="location" placeholder="Location" onChange={handleChange} required />
          </>
        )}
        <input type="text" name="phone" placeholder="Phone Number" onChange={handleChange} required />
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
