import React from "react";

const AboutSection = () => {
  return (
    <section className="py-16 bg-white text-center">
      <h2 className="text-4xl font-bold text-gray-800">About Our Platform</h2>
      <p className="text-lg text-gray-600 mt-4 max-w-3xl mx-auto">
        Our platform connects patients with verified doctors for online consultations, appointments, and easy access to medical prescriptions.
      </p>
    </section>
  );
};

export default AboutSection;
