// src/pages/SurgeryPage/Surgery.js

import React from 'react';
import Navbar from '../../components/HomePage/Navbar';
import './Surgery.css';

import backgroundImage from '../../assets/SurgeryPage/back.png';

// Surgery Icons
import pilesIcon from '../../assets/SurgeryPage/popularSurgeries/piles.png';
import herniaIcon from '../../assets/SurgeryPage/popularSurgeries/hernia.png';
import kidneyStoneIcon from '../../assets/SurgeryPage/popularSurgeries/kidney_stone.png';
import cataractIcon from '../../assets/SurgeryPage/popularSurgeries/cataract.png';
import circumcisionIcon from '../../assets/SurgeryPage/popularSurgeries/circumcision.png';
import lasikIcon from '../../assets/SurgeryPage/popularSurgeries/lasik.png';
import varicoseVeinsIcon from '../../assets/SurgeryPage/popularSurgeries/varicose_veins.png';
import gallstoneIcon from '../../assets/SurgeryPage/popularSurgeries/gallstone.png';
import analFistulaIcon from '../../assets/SurgeryPage/popularSurgeries/anal_fistula.png';
import gynaecomastiaIcon from '../../assets/SurgeryPage/popularSurgeries/gynaecomastia.png';
import analFissureIcon from '../../assets/SurgeryPage/popularSurgeries/anal_fissure.png';
import lipomaRemovalIcon from '../../assets/SurgeryPage/popularSurgeries/lipoma_removal.png';
import sebaceousCystIcon from '../../assets/SurgeryPage/popularSurgeries/sebaceous_cyst.png';
import pilonidalSinusIcon from '../../assets/SurgeryPage/popularSurgeries/pilonidal_sinus.png';
import lumpInBreastIcon from '../../assets/SurgeryPage/popularSurgeries/lump_in_breast.png';
import turpIcon from '../../assets/SurgeryPage/popularSurgeries/turp.png';
import hydroceleIcon from '../../assets/SurgeryPage/popularSurgeries/hydrocele.png';
import kneeReplacementIcon from '../../assets/SurgeryPage/popularSurgeries/knee_replacement.png';
import hairTransplantIcon from '../../assets/SurgeryPage/popularSurgeries/hair_transplant.png';

// Department Icons
import generalSurgeryIcon from '../../assets/SurgeryPage/departments/general_surgery.png';
import proctologyIcon from '../../assets/SurgeryPage/departments/proctology.png';
import ophthalmologyIcon from '../../assets/SurgeryPage/departments/ophthalmology.png';
import urologyIcon from '../../assets/SurgeryPage/departments/urology.png';
import cosmeticIcon from '../../assets/SurgeryPage/departments/cosmetic.png';
import orthopedicsIcon from '../../assets/SurgeryPage/departments/orthopedics.png';

const Surgery = () => {
  return (
    <>
      <Navbar />

      <div className="surgery-page">
        {/* Header with background image */}
        <div className="surgery-header-wrapper">
          <div className="surgery-header">
            <img
              src={backgroundImage}
              alt="Surgery Background"
              className="surgery-background"
            />
          </div>
          <div className="surgery-header-caption">
            <h1>We are experts in Surgical solutions for 50+ ailments.</h1>
          </div>
        </div>

        {/* Popular Surgeries */}
        <div className="popular-surgeries section">
          <h2>Popular Surgeries</h2>
          <div className="surgeries-box">
            <div className="surgeries-grid">
              {[
                { name: "Piles", icon: pilesIcon },
                { name: "Hernia Treatment", icon: herniaIcon },
                { name: "Kidney Stone", icon: kidneyStoneIcon },
                { name: "Cataract", icon: cataractIcon },
                { name: "Circumcision", icon: circumcisionIcon },
                { name: "Lasik", icon: lasikIcon },
                { name: "Varicose Veins", icon: varicoseVeinsIcon },
                { name: "Gallstone", icon: gallstoneIcon },
                { name: "Anal Fistula", icon: analFistulaIcon },
                { name: "Gynaecomastia", icon: gynaecomastiaIcon },
                { name: "Anal Fissure", icon: analFissureIcon },
                { name: "Lipoma Removal", icon: lipomaRemovalIcon },
                { name: "Sebaceous cyst", icon: sebaceousCystIcon },
                { name: "Pilonidal Sinus", icon: pilonidalSinusIcon },
                { name: "Lump in Breast", icon: lumpInBreastIcon },
                { name: "TURP", icon: turpIcon },
                { name: "Hydrocele", icon: hydroceleIcon },
                { name: "Knee Replacement", icon: kneeReplacementIcon },
                { name: "Hair Transplant", icon: hairTransplantIcon }
              ].map((surgery, index) => (
                <div key={index} className="surgery-item">
                  <img src={surgery.icon} alt={surgery.name} className="surgery-icon" />
                  <h3>{surgery.name}</h3>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Departments Section */}
        <div className="departments-section section">
          <h2>Our Departments</h2>
          <div className="departments-box">
            <div className="departments-grid">
              {[
                { name: "General Surgery", icon: generalSurgeryIcon, ailments: "9 ailments" },
                { name: "Proctology", icon: proctologyIcon, ailments: "5 ailments" },
                { name: "Ophthalmology", icon: ophthalmologyIcon, ailments: "4 ailments" },
                { name: "Urology", icon: urologyIcon, ailments: "12 ailments" },
                { name: "Cosmetic", icon: cosmeticIcon, ailments: "6 ailments" },
                { name: "Orthopedics", icon: orthopedicsIcon, ailments: "8 ailments" }
              ].map((dept, index) => (
                <div key={index} className="department-item">
                  <img src={dept.icon} alt={dept.name} className="department-icon" />
                  <h3>{dept.name}</h3>
                  <p>{dept.ailments}</p>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* How It Works */}
        <div className="how-it-works section">
          <h2>How It Works</h2>
          <div className="steps">
            {[
              ["Connect with us", "A dedicated assistant will guide you at every step."],
              ["Schedule Consultation", "Consult with an expert doctor near you."],
              ["Diagnosis & Treatment", "Get Diagnosis & Treatment advice from expert doctors."],
              ["Surgery Recommendation", "In some cases, a surgery might be recommended."],
              ["Choose a Hospital", "Choose one of our partner hospitals that fits your needs."],
              ["Complete Assistance", "From Admission to Discharge – including travel, insurance paperwork, and pre-surgery tests."],
              ["Post-Operative Support", "Receive follow-up consultations and post-operative care."]
            ].map(([title, desc], i) => (
              <div key={i} className="step">
                <h3>{i + 1}. {title}</h3>
                <p>{desc}</p>
              </div>
            ))}
          </div>
        </div>

        {/* Surgery Benefits */}
        <div className="surgery-benefits section">
          <h2>Practo Care Benefits</h2>
          <div className="benefits">
            {[
              "Expert surgeons, qualified & experienced specialists.",
              "Advanced surgical procedures with cutting-edge technologies.",
              "Personal care assistants for guidance and post-operative care.",
              "Financial aid and assistance with transparent pricing."
            ].map((text, i) => (
              <div key={i} className="benefit-item">
                <h3>{text}</h3>
              </div>
            ))}
          </div>
        </div>

        {/* Registration Form */}
        <div className="registration-form">
          <h2>Register for Surgery Consultation</h2>
          <form>
            <input type="text" placeholder="Name" />
            <select>
              <option value="surgeryType">Select Type of Surgery</option>
              <option value="hernia">Hernia</option>
              <option value="kidneyStone">Kidney Stone</option>
              <option value="cataract">Cataract</option>
            </select>
            <input type="tel" placeholder="Phone Number" />
            <select>
              <option value="city">Select City</option>
              <option value="city1">City 1</option>
              <option value="city2">City 2</option>
            </select>
            <button type="submit">Register</button>
          </form>
        </div>
      </div>
    </>
  );
};

export default Surgery;
