import React, { useState } from 'react';
import SurgeryHeader from './SurgeryHeader';
import PopularSurgeries from './PopularSurgeries';
import Departments from './Departments';
import VideoSection from './VideoSection';
import HowItWorks from './HowItWorks';
import Benefits from './Benefits';
import RegistrationForm from './RegistrationForm';
import SurgeryBookingForm from './SurgeryBookingForm';
import SurgeryPopupForm from './SurgeryPopupForm'; // ✅ 1. Import the component


import '../../styles/SurgeryPage/Surgery.css';

const SurgeryPage = () => {
  const [selectedSurgery, setSelectedSurgery] = useState(null); // ✅ 2. Control popup state

  const handleSurgeryClick = (surgeryName) => {
    setSelectedSurgery(surgeryName);
  };

  const handleClosePopup = () => {
    setSelectedSurgery(null);
  };

  return (
    <div className="surgery-page">
      <SurgeryHeader />

      <div className="surgery-main-section">
        <PopularSurgeries onSurgeryClick={handleSurgeryClick} /> {/* ✅ 3. Pass handler */}
        <RegistrationForm />
      </div>

      <Departments />
      <VideoSection />
      <HowItWorks />
      <Benefits />
      <SurgeryBookingForm />

      {selectedSurgery && (
        <SurgeryPopupForm
          surgery={selectedSurgery}
          onClose={handleClosePopup}
        />
      )} {/* ✅ 4. Conditionally render the popup */}
    </div>
  );
};

export default SurgeryPage;
