import React, { useState } from 'react';
import '../../styles/HomePage/CallToAction.css';

const faqs = [
  {
    question: 'How does online consultation work?',
    answer: 'You can select a specialty, book a slot, and consult a doctor via audio/video call.'
  },
  {
    question: 'Is my consultation private?',
    answer: 'Yes, all consultations are 100% private and confidential.'
  },
  {
    question: 'Can I get a prescription after consultation?',
    answer: 'Yes, you will receive a digital prescription after your consultation.'
  },
  {
    question: 'How do I make a payment?',
    answer: 'Payments can be made securely through our app or website.'
  },
  {
    question: 'Can I reschedule my appointment?',
    answer: 'Yes, appointments can be rescheduled easily from your dashboard.'
  },
  {
    question: 'Are all doctors verified?',
    answer: 'Yes, all doctors on our platform are verified professionals.'
  },
];

const CallToAction = () => {
  const [openIndex, setOpenIndex] = useState(null);

  const toggleFAQ = (index) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  const firstColumn = faqs.slice(0, 3);
  const secondColumn = faqs.slice(3, 6);

  return (
    <div className="cta-section">
      <h2 className="cta-title">FAQs</h2>
      <div className="faq-columns">
        {[firstColumn, secondColumn].map((column, colIndex) => (
          <div key={colIndex} className="faq-column">
            {column.map((faq, index) => (
              <div key={index} className="faq-item" onClick={() => toggleFAQ(colIndex * 3 + index)}>
                <div className="faq-question-container">
                  <span className={`arrow ${openIndex === (colIndex * 3 + index) ? 'open' : ''}`}>&#9662;</span>
                  <span className="faq-question">{faq.question}</span>
                </div>
                {openIndex === (colIndex * 3 + index) && <div className="faq-answer">{faq.answer}</div>}
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default CallToAction;
