import React, { useState } from "react";
import Step1BasicDetails from "./Step1BasicDetails";
import Step2PharmacyDetails from "./Step2BusinessDetails";
import Step3UploadDocuments from "./Step3UploadDocuments";
import Step4ReviewSubmit from "./Step4ReviewSubmit";
import "../../styles/PharmacyRegistration.css";

const MultiStepForm = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    phone: "",
    licenseNumber: "",
    pharmacyName: "",
    pharmacyAddress: "",
    licenseCertificate: null,
    ownerPhoto: null,
  });

  const handleChange = (e) => {
    const { name, value, type, files } = e.target;
    if (type === "file") {
      setFormData((prevData) => ({ ...prevData, [name]: files[0] }));
    } else {
      setFormData((prevData) => ({ ...prevData, [name]: value }));
    }
  };

  const nextStep = () => setStep(step + 1);
  const prevStep = () => setStep(step - 1);

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Pharmacy Registration Submitted Successfully!");
    console.log("Form Data:", formData);
  };

  return (
    <div className="pharmacy-registration-container">
      {step === 1 && (
        <Step1BasicDetails nextStep={nextStep} handleChange={handleChange} formData={formData} />
      )}
      {step === 2 && (
        <Step2BusinessDetails nextStep={nextStep} prevStep={prevStep} handleChange={handleChange} formData={formData} />
      )}
      {step === 3 && (
        <Step3UploadDocuments nextStep={nextStep} prevStep={prevStep} handleChange={handleChange} formData={formData} />
      )}
      {step === 4 && (
        <Step4ReviewSubmit prevStep={prevStep} formData={formData} handleSubmit={handleSubmit} />
      )}
    </div>
  );
};

export default MultiStepForm;
