import React, { useState } from "react";
import Step1BasicDetails from "./Step1BasicDetails";
import Step2ProfessionalInfo from "./Step2ProfessionalInfo";
import Step3Uploads from "./Step3Uploads";
import Step4Review from "./Step4Review";
import ProgressBar from "./ProgressBar";

const MultiStepForm = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    phone: "",
    licenseNumber: "",
    specialization: "",
    experience: "",
    licensePdf: null,
    photo: null,
  });

  const nextStep = () => setStep(step + 1);
  const prevStep = () => setStep(step - 1);
  
  return (
    <div className="max-w-2xl mx-auto p-6 bg-white shadow-md rounded-lg">
      <ProgressBar step={step} />
      {step === 1 && <Step1BasicDetails nextStep={nextStep} formData={formData} setFormData={setFormData} />}
      {step === 2 && <Step2ProfessionalInfo nextStep={nextStep} prevStep={prevStep} formData={formData} setFormData={setFormData} />}
      {step === 3 && <Step3Uploads nextStep={nextStep} prevStep={prevStep} formData={formData} setFormData={setFormData} />}
      {step === 4 && <Step4Review prevStep={prevStep} formData={formData} />}
    </div>
  );
};

export default MultiStepForm;
