import React from "react";
import Navbar from "../components/Navbar";
import Services from "../components/Services";
import Testimonials from "../components/Testimonials";
import Contact from "../components/Contact";
import heroImage from "../assets/hero-image.jpg"; // Add an actual image

const Home = () => {
  return (
    <div>
      <Navbar />
      
      {/* Hero Section */}
      <header className="flex flex-col md:flex-row items-center justify-between bg-gray-100 h-screen p-12">
        <img src={heroImage} alt="Healthcare" className="w-1/2 rounded-lg shadow-md" />
        <div className="text-center md:text-left md:w-1/2">
          <h1 className="text-4xl font-bold text-blue-600">MediCare</h1>
          <p className="text-gray-700 mt-4 text-lg font-semibold">"Your Health, Our Priority"</p>
        </div>
      </header>

      {/* About Section */}
      <section className="p-12 bg-white text-center">
        <h2 className="text-3xl font-bold text-gray-800">About Us</h2>
        <p className="text-gray-600 mt-4 text-lg">
          We provide online consultations, appointment bookings, and digital prescriptions to make healthcare accessible for everyone.
        </p>
      </section>

      {/* Services Section */}
      <Services />

      {/* Testimonials Section */}
      <Testimonials />

      {/* Contact Section */}
      <Contact />
    </div>
  );
};

export default Home;
