import React from "react";

const testimonials = [
  { name: "Dr. Smith", role: "Doctor", feedback: "Great platform for managing patients online." },
  { name: "John Doe", role: "Patient", feedback: "Easy and quick to consult a doctor!" },
];

const Testimonials = () => {
  return (
    <section className="w-full px-8 py-20 bg-white text-center">
      <h2 className="text-4xl font-bold text-gray-800">What Our Doctors & Patients Say</h2>
      <div className="mt-10 grid grid-cols-1 md:grid-cols-2 gap-12 max-w-6xl mx-auto">
        {testimonials.map((testi, index) => (
          <div key={index} className="p-8 bg-gray-50 rounded-xl shadow-md hover:shadow-xl transition-all duration-300">
            <h3 className="text-2xl font-bold">{testi.name}</h3>
            <p className="text-gray-500 text-lg">{testi.role}</p>
            <p className="text-gray-600 mt-4 text-xl">"{testi.feedback}"</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Testimonials;
