// src/components/DoctorSearch/DoctorSearchNavbar.jsx
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Navbar from "../HomePage/Navbar"; // reuse your homepage navbar
import "../DoctorSearch.css"; // styling handled separately

const DoctorSearchNavbar = () => {
  const [location, setLocation] = useState("");
  const [specialization, setSpecialization] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    if (location && specialization) {
      const encodedLoc = encodeURIComponent(location);
      const encodedSpec = encodeURIComponent(specialization);
      navigate(`/doctors?city=${encodedLoc}&specialization=${encodedSpec}`);
    }
  }, [location, specialization, navigate]);

  return (
    <div>
      <Navbar />
      <div className="search-container">
        <div className="search-box">
          <div className="search-section">
            <select
              value={location}
              onChange={(e) => setLocation(e.target.value)}
            >
              <option value="">Select Location</option>
              <option value="Mumbai">Mumbai</option>
              <option value="Delhi">Delhi</option>
              <option value="Hyderabad">Hyderabad</option>
              {/* Add more cities */}
            </select>
          </div>
          <div className="divider" />
          <div className="search-section">
            <select
              value={specialization}
              onChange={(e) => setSpecialization(e.target.value)}
            >
              <option value="">Select Specialization</option>
              <option value="General Physician">General Physician</option>
              <option value="Cardiologist">Cardiologist</option>
              <option value="Dermatologist">Dermatologist</option>
              {/* Add more specialties */}
            </select>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DoctorSearchNavbar;
