import React from "react";

const testimonials = [
  { name: "Dr. Smith", role: "Doctor", feedback: "Great platform for managing patients online." },
  { name: "John Doe", role: "Patient", feedback: "Easy and quick to consult a doctor!" },
];

const Testimonials = () => {
  return (
    <section className="p-12 bg-white text-center">
      <h2 className="text-3xl font-bold text-gray-800">What Our Doctors & Patients Say</h2>
      <div className="mt-8 grid grid-cols-1 md:grid-cols-2 gap-8">
        {testimonials.map((testi, index) => (
          <div key={index} className="p-6 bg-gray-100 rounded-lg shadow-lg">
            <h3 className="text-xl font-bold">{testi.name}</h3>
            <p className="text-gray-500 text-sm">{testi.role}</p>
            <p className="text-gray-600 mt-2">"{testi.feedback}"</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Testimonials;
