import React, { useState } from 'react';
import { XCircle, Stethoscope } from 'lucide-react';
import { ailments } from './ailmentsData'; // adjust the path as needed

const RegistrationForm = () => {
  const [showAilmentPopup, setShowAilmentPopup] = useState(false);
  const [selectedCity, setSelectedCity] = useState('Bengaluru');
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [selectedMainCategory, setSelectedMainCategory] = useState(0); // Default to first category
  const [selectedSubcategory, setSelectedSubcategory] = useState(null);

  const handleSubcategorySelect = (sub) => {
    setSelectedSubcategory(sub);
    setShowAilmentPopup(false); // Close popup on selection
  };

  return (
    <div className="registration-sticky-wrapper">
      <div className="registration-form">
        <h2 className="form-title">Book your consultation today</h2>

        {/* City Dropdown */}
        <select
          value={selectedCity}
          onChange={(e) => setSelectedCity(e.target.value)}
          className="form-input"
        >
          <option value="Bengaluru">Bengaluru</option>
          <option value="Delhi">Delhi</option>
          <option value="Mumbai">Mumbai</option>
        </select>

        {/* Select Ailment Button */}
        <button
          className="form-input"
          onClick={() => {
            setSelectedMainCategory(0); // Show default category when opened
            setShowAilmentPopup(true);
          }}
          style={{
            textAlign: 'left',
            color: selectedSubcategory ? '#000' : '#888',
            fontWeight: selectedSubcategory ? 'bold' : 'normal'
          }}
        >
          {selectedSubcategory || 'Select Ailment'}
        </button>

        {/* Ailment Popup */}
        {showAilmentPopup && (
          <div className="ailment-popup">
            <div className="ailment-container">
              <button className="close-button" onClick={() => setShowAilmentPopup(false)}>
                <XCircle size={24} />
              </button>

              <div className="popup-title">Select your Surgical Ailment</div>

              <div className="ailment-columns">
                {/* Left Column: Main Categories */}
                <div className="ailment-left scrollable-x">
                  {ailments.map((cat, i) => (
                    <div
                      key={i}
                      className={`main-category-item ${selectedMainCategory === i ? 'selected' : ''}`}
                      onClick={() => setSelectedMainCategory(i)}
                    >
                      <div className="icon-round">
                        <Stethoscope size={18} />
                      </div>
                      <div className="category-texts">
                        <div className="category-title">{cat.mainCategory}</div>
                        <div className="category-sub">{cat.department}</div>
                      </div>
                    </div>
                  ))}
                </div>

                {/* Right Column: Subcategories */}
                <div className="ailment-right scrollable-x">
                  {selectedMainCategory !== null && (
                    <div className="ailment-column">
                      <h4 className="ailment-dept">{ailments[selectedMainCategory].department}</h4>
                      <ul className="ailment-sublist horizontal-sublist">
                        {ailments[selectedMainCategory].subcategories.map((sub, j) => (
                          <li
                            key={j}
                            className={`ailment-subitem ailment-box ${
                              selectedSubcategory === sub ? 'selected-subitem' : ''
                            }`}
                            onClick={() => handleSubcategorySelect(sub)}
                          >
                            <div className="icon-placeholder" />
                            {sub}
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Other Inputs */}
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="form-input"
        />
        <input
          type="text"
          placeholder="Phone number"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          className="form-input"
        />
        <input
          type="text"
          placeholder="Age"
          className="form-input"
        />

        <button className="form-submit">Book Appointment</button>
      </div>
    </div>
  );
};

export default RegistrationForm;
