import React, { useState } from 'react';
import { Phone } from 'lucide-react';
import '../../styles/SurgeryPage/RegistrationForm.css';

const ailments = [
  {
    mainCategory: 'Knee Replacement related',
    department: 'Orthopedics',
    subcategories: [
      'Knee Replacement',
      'Arthroscopy',
      'Carpal Tunnel Syndrome',
      'Lumbar Laminectomy',
      'ACL Repair',
      'Microdiscectomy',
      'Knee Arthroscopy',
      'Shoulder Arthroscopy'
    ]
  },
  {
    mainCategory: 'Eye related',
    department: 'Ophthamology',
    subcategories: [
      'Cataract',
      'Lasik',
      'Glaucoma',
      'Squinteye'
    ]
  },
  {
    mainCateogry: 'General Surgery related',
    department: 'General Surgery',
    subcategories: [
      'Hernia Treatment',
      'Gallstone',
      'Appendectomy',
      'Lipoma Removal',
      'Circumcision',
      'Sebaceous Cyst',
      'Varicose Veins',
      'Varicocele'
    ]}
];

const RegistrationForm = () => {
  const [showAilmentPopup, setShowAilmentPopup] = useState(false);
  const [selectedCity, setSelectedCity] = useState('Bengaluru');
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');

  return (
    <div className="registration-wrapper">
      <div className="registration-form">
        <h2 className="form-title">Book your consultation today</h2>
        <select value={selectedCity} onChange={e => setSelectedCity(e.target.value)} className="form-input">
          <option value="Bengaluru">Bengaluru</option>
          <option value="Delhi">Delhi</option>
          <option value="Mumbai">Mumbai</option>
        </select>

        <button className="form-input" onClick={() => setShowAilmentPopup(true)}>Select Ailment</button>

        {showAilmentPopup && (
          <div className="ailment-popup">
            <div className="ailment-sidebar">
              {ailments.map((cat, i) => (
                <div key={i} className="ailment-category">
                  <div className="icon-placeholder" />
                  <span>{cat.mainCategory}</span>
                </div>
              ))}
            </div>
            <div className="ailment-list">
              {ailments.map((cat, i) => (
                <div key={i}>
                  <h4>{cat.department}</h4>
                  <ul>
                    {cat.subcategories.map((sub, j) => (
                      <li key={j}><div className="icon-placeholder" />{sub}</li>
                    ))}
                  </ul>
                </div>
              ))}
            </div>
          </div>
        )}

        <input className="form-input" type="text" placeholder="Name" value={name} onChange={e => setName(e.target.value)} />
        <input className="form-input" type="text" placeholder="Phone number" value={phone} onChange={e => setPhone(e.target.value)} />
        <button className="submit-btn">Book Appointment</button>
      </div>

      <div className="or-separator">or</div>

      <div className="reachout-box">
        Reach out to us on &nbsp;|&nbsp; <Phone size={18} /> 08045685554
      </div>
    </div>
  );
};

export default RegistrationForm;
