// pages/HomePage/Testimonials.js
import React from "react";
import TestimonialCard from "../../components/HomePage/TestimonialCard";
import "../../styles/HomePage/Testimonials.css";

const testimonials = [
  {
    name: "Anita Sharma",
    message: "Very helpful platform. I could consult a doctor and get medicine at home!",
    avatar: "user1.jpg",
  },
  {
    name: "Ravi Kumar",
    message: "Easy booking and quick reports. Saved me time and effort!",
    avatar: "user2.jpg",
  },
  {
    name: "Fatima Sheikh",
    message: "Reliable and affordable. Highly recommend for rural families.",
    avatar: "user3.jpg",
  },
];

const Testimonials = () => {
  return (
    <section className="testimonials-section">
      <h2>What Our Users Say</h2>
      <div className="testimonial-cards">
        {testimonials.map((user, index) => (
          <TestimonialCard key={index} name={user.name} message={user.message} avatar={user.avatar} />
        ))}
      </div>
    </section>
  );
};

export default Testimonials;
